mapper.select:
SELECT *
FROM reservation
WHERE active_status = 'ACTIVE'
AND status = 'CONFIRMED'
AND start_at  <  #{endAt}
AND end_at    >  #{startAt}

SELECT *
FROM reservation
WHERE room_id = #{roomId}
AND active_status = 'ACTIVE'
AND status = 'CONFIRMED'
AND start_at  <  #{endAt}
AND end_at    >  #{startAt}
FOR UPDATE

mapper.update:
UPDATE reservation
SET active_status = 'INACTIVE' , status = 'CANCELED'
WHERE reservation_id = #{id}

mapper.select:
SELECT rv.reservation_id as reservationId, m.member_id as memberId, m.name as name, m.email as email, rv.start_at as startAt, rv.end_at as endAt, rv.duration as duration, rv.status as status, rv.created_at as createdAt
FROM reservation rv
JOIN member m ON rv.member_id = m.member_id
rv.room_id = #{roomId} AND rv.active_status = 'ACTIVE'
AND m.email = #{cond.memberEmail}
AND rv.status = #{cond.status}
AND DATE(rv.start_at) = #{cond.date}
ORDER BY rv.created_at
ASC DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(rv.reservation_id)

FROM reservation rv

JOIN member m
ON rv.member_id = m.member_id


    rv.room_id = #{roomId}
    AND rv.active_status = 'ACTIVE'


        AND m.email = #{cond.memberEmail}



        AND rv.status = #{cond.status}



        AND DATE(rv.start_at) = #{cond.date}

SELECT rv.reservation_id as reservationId, m.member_id as memberId, m.name as name, m.email as email, rv.start_at as startAt, rv.end_at as endAt, rv.duration as duration, rv.status as status, rv.created_at as createdAt
FROM reservation rv
JOIN member m ON rv.member_id = m.member_id
rv.active_status = 'ACTIVE' AND rv.status = 'CONFIRMED'
AND rv.room_id = #{roomId}
LIMIT #{limit} OFFSET #{offset}
